import React from 'react';
import './ChatInfo.css';
import {FaPaperPlane} from 'react-icons/fa';


class ChatInfo extends React.Component{
	constructor(props){
		super(props);
		this.state = {
				messageArray: [
					{
						sender: "admin",
						message: "Hi, I am admin",
					},
					{
						sender:"appl",
						message: "Hi admin",
					},
					{
						sender: "admin",
						message: "aaaaaaaaaa",
					},
					{
						sender:"appl",
						message: "bbbbbbbb",
					},
					{
						sender: "admin",
						message: "cccccccccc",
					},
					{
						sender:"appl",
						message: "dddddddd",
					}
				],
		};
	}
	render(){
		return(
			<div className="chatDiv">

			    <div className="chatHeader" >
					<p className="textCss3">Chat</p>
					<p>Active</p>
				</div>
				<div className="chatContainer">
					{this.state.messageArray.map(s => 
						<section>
						<p>{s.sender}</p>
						<p>{s.message}</p>
						</section>
					)}

				</div>

            </div>
			);
	}

}

export default ChatInfo;
