import React from 'react';
import ReactDOM from 'react-dom';
import styles from './applicantInfo.module.css';
import {MdDelete} from 'react-icons/md';
import {FaUserEdit} from 'react-icons/fa';
import './ApplicantInfo.css';


class ApplicantInfo extends React.Component {
	constructor(props){
		super(props);
		this.deleteApplicant = this.deleteApplicant.bind(this);
		this.editApplicant = this.editApplicant.bind(this);
		this.closeForm = this.closeForm.bind(this);
		this.state = {
				showEditForm: false,
		};
	}

	deleteApplicant(){
		alert("Delete "+ this.props.info.name);
	}
	editApplicant(){
		alert("Edit "+this.props.info.name);
		this.setState({showEditForm: true});
	}
	closeForm(){
		this.setState({showEditForm: false});
	}


	render(){
		let editForm = null; 
		if(this.state.showEditForm){
			editForm = 
			<div class="formPopup">
			  <form  class="formContainer">
			    <h1>Edit</h1>
			    <label><b>Name</b></label>
			    <input type="text" placeholder="Enter name"/>
			    <button type="submit" class="btn cancel" onClick={this.closeForm}>Close</button>
			  </form>
			</div>
		}
		return(
		  <section>
		  <div className={styles.divEl} onClick={this.props.triggerParentUpdate}>
		    <div className={styles.imgDiv}>
		    	<img className={styles.applImg} src={this.props.info.photoURL} alt="Applicant photo"/>
		    </div>
		    <div className={styles.infoDiv}>
		    	<p className={styles.textCss2}>{this.props.info.name}</p>
		    	<p className={styles.textCss3}>{this.props.info.placeOfEmployment}</p>
		    	<p className={styles.textCss1}>{this.props.info.addres}</p>
		    </div>
		  </div>
		  <div>
	    	<button className="deleteBtn btnFont" onClick={this.deleteApplicant} ><MdDelete/> Delete</button>
	    	<button className="editBtn btnFont" onClick={this.editApplicant}><FaUserEdit/> Edit</button>
	       </div>
	       {editForm}
	       </section>
		);
	}
} 

export default ApplicantInfo;
