import React from 'react';
import ReactDOM from 'react-dom';
import ApplicantInfo from './ApplicantInfo.js';
import styles from './applicantList.module.css';
import ClientDemo from './ClientDemo';
import './ApplicantList.css';
//import Client from './Client';


class ApplicantList extends React.Component {
	constructor(props){
		super(props);
		this.state = {
				applicants: [],
				moreInfo: false,
				classStyle: 'listDiv',
		}; 
		this.showMoreInfo = this.showMoreInfo.bind(this);
		this.clientDemo = new ClientDemo();
		this.clientDemo.applicantList().then(l => this.setState({applicants: l}));
		//clientDemo demonstrate client request and return array of applicants from base
	}
	showMoreInfo(){
		this.setState({moreInfo: true ,classStyle: 'listDiv2',});
	}

  render(){
	let applMoreInfo = null;
	if(this.state.moreInfo){ applMoreInfo = <p>more info...</p>;}

    return(
    		<div className="mainListDiv">
    			<div className="listDiv1">
    				<p className={styles.textCss1}> Showing {this.state.applicants.length} results</p>
    			    {this.state.applicants.map(a => <ApplicantInfo info={a} triggerParentUpdate={this.showMoreInfo}/>)}   
    			</div>
    			<div>
    				{applMoreInfo}
    			</div>
    		</div> 
    );
  }
}

export default ApplicantList;
